import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import TodoItem from "./TodoItem";

const Todo = () => {
  const [todos, setTodos] = useState([]);
  const [inputValue, setInputValue] = useState("");

  return (
    <div>
      <input
        type="text"
        name="todo-input"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
      />
      <button
        onClick={() => {
          const id = uuidv4();
          const myTodos = [...todos, { id: id, value: inputValue }];
          setInputValue("");
          setTodos(myTodos);
        }}
      >
        Submit
      </button>
      <ul>
        {todos.map((todo) => {
          return <TodoItem key={todo.id} todo={todo} bla="bla" />;
        })}
      </ul>
    </div>
  );
};

export default Todo;
