  // const incrementBtn = () => {
  //   setCounter(counter + 1);
  // };
  // const decrementBtn = () => {
  //   setCounter(counter - 1);
  // };

  return (
    <li key={product.id}>
      {product.value}
      <button onClick={() => setInputValue(product)}>Update</button>
      <button
        onClick={() => {
          const filteredTodos = products.filter((td) => td.id !== product.id);
          setProducts(filteredTodos);
        }}
      >
        Delete
      </button>
      {counter}

      {/* <div className="buttons">
        <button onClick={incrementBtn}>+</button>
        <button onClick={decrementBtn}>-</button>
      </div> */}
    </li>
  );
};

export default Product;
