import React from "react";

const Product = ({ todo, setProducts, products, setInputValue }) => {
  return (
    <li key={todo.id}>
      {todo.value}
      <button onClick={() => setInputValue(todo)}>Update</button>
      <button
        onClick={() => {
          const filteredTodos = products.filter((td) => td.id !== todo.id);
          setProducts(filteredTodos);
        }}
      >
        Delete
      </button>
    </li>
  );
};

export default Product;
