import React, { useState } from "react";

const Product = ({ product, setProducts, products, setInputValue }) => {
  const [counter, setCounter] = useState(1);

  const increment = () => {
    setCounter(counter + 1);
  };
  const decrement = () => {
    setCounter(Math.max(counter - 1, 1));
  };

  return (
    <li key={product.id} className="listItem">
      {product.value}
      <div className="options">
        <button className="optionItem" onClick={() => setInputValue(product)}>Update</button>
        <button className="optionItem"
          onClick={() => {
            const filteredProducts = products.filter(
              (td) => td.id !== product.id
            );
            setProducts(filteredProducts);
          }}
        >
          Delete
        </button>
        <div className="optionItem counter">
          <button className="min" onClick={decrement}>
            -
          </button>
          {counter}
          <button onClick={increment}>+</button>
        </div>
      </div>
    </li>
  );
};

export default Product;
